  🎮 开始游戏




(() => {
  const gameSize = 20;
  const game = document.getElementById("game");
  const startBtn = document.getElementById("startBtn");
  const scoreEl = document.getElementById("score");

  let snake, direction, apple, score, intervalId;

  const modal = document.createElement("div");
  modal.id = "game-over-modal";
  modal.innerHTML = `Game Over重新开始`;
  game.appendChild(modal);

  const createGrid = () => {
    game.innerHTML = '';
    game.appendChild(modal);
    game.style.gridTemplateColumns = `repeat(${gameSize}, 1fr)`;
    game.style.gridTemplateRows = `repeat(${gameSize}, 1fr)`;
    for (let i = 0; i < gameSize * gameSize; i++) {
      const cell = document.createElement("div");
      game.appendChild(cell);
    }
  };

  const draw = () => {
    const allCells = Array.from(game.children).filter(el => el !== modal);
    allCells.forEach(cell => cell.style.background = "#fff");

    snake.forEach(({ x, y }) => {
      const idx = y * gameSize + x;
      if (allCells[idx]) allCells[idx].style.background = "#4caf50";
    });

    const appleIdx = apple.y * gameSize + apple.x;
    if (allCells[appleIdx]) allCells[appleIdx].style.background = "#f44336";
  };

  const placeApple = () => {
    let newApple;
    do {
      newApple = {
        x: Math.floor(Math.random() * gameSize),
        y: Math.floor(Math.random() * gameSize)
      };
    } while (snake.some(seg => seg.x === newApple.x && seg.y === newApple.y));
    apple = newApple;
  };

  const move = () => {
    const head = { ...snake[0] };
    head.x += direction.x;
    head.y += direction.y;

    if (
      head.x < 0 || head.y < 0 ||
      head.x >= gameSize || head.y >= gameSize ||
      snake.some(seg => seg.x === head.x && seg.y === head.y)
    ) {
      clearInterval(intervalId);
      modal.style.display = "block";
      startBtn.style.display = "inline-block";
      return;
    }

    snake.unshift(head);

    if (head.x === apple.x && head.y === apple.y) {
      score++;
      scoreEl.textContent = score;
      placeApple();
    } else {
      snake.pop();
    }

    draw();
  };

  const startGame = () => {
    snake = [{ x: 10, y: 10 }];
    direction = { x: 1, y: 0 };
    score = 0;
    scoreEl.textContent = score;
    placeApple();
    draw();
    clearInterval(intervalId);
    intervalId = setInterval(move, 150);
    startBtn.style.display = "none";
    modal.style.display = "none";
  };

  // ⌨️ 键盘控制 + 禁止页面滚动
  document.addEventListener("keydown", e => {
    if (["ArrowUp", "ArrowDown", "ArrowLeft", "ArrowRight"].includes(e.key)) {
      e.preventDefault(); // ✅ 禁止方向键引发滚动
    }

    if (e.key === "ArrowUp" && direction.y !== 1) direction = { x: 0, y: -1 };
    else if (e.key === "ArrowDown" && direction.y !== -1) direction = { x: 0, y: 1 };
    else if (e.key === "ArrowLeft" && direction.x !== 1) direction = { x: -1, y: 0 };
    else if (e.key === "ArrowRight" && direction.x !== -1) direction = { x: 1, y: 0 };
  }, { passive: false });

  // 📱 触摸滑动控制
  let touchStartX = 0, touchStartY = 0;
  game.addEventListener("touchstart", e => {
    const touch = e.touches[0];
    touchStartX = touch.clientX;
    touchStartY = touch.clientY;
  });
  game.addEventListener("touchend", e => {
    const touch = e.changedTouches[0];
    const dx = touch.clientX - touchStartX;
    const dy = touch.clientY - touchStartY;
    if (Math.abs(dx) > Math.abs(dy)) {
      if (dx > 0 && direction.x !== -1) direction = { x: 1, y: 0 };
      else if (dx < 0 && direction.x !== 1) direction = { x: -1, y: 0 };
    } else {
      if (dy > 0 && direction.y !== -1) direction = { x: 0, y: 1 };
      else if (dy < 0 && direction.y !== 1) direction = { x: 0, y: -1 };
    }
  });

  // 防止触摸滑动页面
  ['touchstart', 'touchmove', 'touchend'].forEach(evt =>
    document.body.addEventListener(evt, e => {
      if (e.target.closest('#game')) e.preventDefault();
    }, { passive: false })
  );

  startBtn.addEventListener("click", startGame);
  createGrid();
})();


