/* —— 作用域到 #snake-cute，避免污染博客全局 —— */
#snake-cute{ --bg1:#fff8fb; --bg2:#f0fbff; --panel:rgba(255,255,255,.78); --bd:rgba(0,0,0,.06); --ink:#1f2937; --muted:#6b7280; --green:#22c55e; --red:#ef4444; --vio:#a78bfa; --pink:#f472b6; --amber:#f59e0b; --blue:#60a5fa; --gap:3px; --shadow:0 10px 30px rgba(31,41,55,.12), 0 2px 10px rgba(31,41,55,.06);} 
#snake-cute{font-family: ui-sans-serif, -apple-system, "Segoe UI", Roboto, PingFangSC, "Noto Sans CJK SC", "Microsoft YaHei", Arial, sans-serif; color:var(--ink);} 
#snake-cute .sc-wrap{max-width:920px;margin:18px auto;padding:12px;background:radial-gradient(1200px 600px at 10% 10%,var(--bg1),#ffffff 60%), radial-gradient(900px 500px at 90% 20%,var(--bg2),#ffffff 70%);}  
#snake-cute .sc-panel{background:var(--panel);backdrop-filter:saturate(140%) blur(10px);border:1px solid var(--bd);border-radius:18px;box-shadow:var(--shadow);padding:14px}
#snake-cute .sc-head{display:flex;align-items:center;justify-content:space-between;gap:12px;flex-wrap:wrap}
#snake-cute .sc-title{display:flex;align-items:center;gap:10px;font-weight:900;letter-spacing:.4px}
#snake-cute .sc-chip{font-size:.8rem;color:#fff;background:linear-gradient(135deg,var(--vio),#60a5fa);padding:2px 10px;border-radius:999px}
#snake-cute .sc-sub{color:var(--muted);font-size:.92rem}
#snake-cute .sc-bar{display:flex;align-items:center;gap:10px;flex-wrap:wrap}
#snake-cute .sc-btn{appearance:none;border:none;cursor:pointer;user-select:none;padding:10px 14px;border-radius:999px;font-weight:800;letter-spacing:.3px;background:#111;color:#fff;transition:transform .08s ease,box-shadow .2s ease,opacity .2s ease;background:linear-gradient(135deg,#111,#334155)}
#snake-cute .sc-btn:hover{transform:translateY(-1px)}
#snake-cute .sc-btn:active{transform:translateY(0)}
#snake-cute .sc-btn.sc-sweet{background:linear-gradient(135deg,#34d399,#22c55e)}
#snake-cute .sc-btn.sc-ghost{background:#fff;color:#111;border:1px solid var(--bd)}
#snake-cute .sc-btn.sc-pastel{background:linear-gradient(135deg,#f9a8d4,#a78bfa)}
#snake-cute .sc-meta{display:flex;align-items:center;gap:12px;color:var(--muted);margin-top:6px;flex-wrap:wrap}
#snake-cute .sc-kv{display:flex;align-items:center;gap:8px;background:#fff;border:1px solid var(--bd);border-radius:12px;padding:8px 10px}
#snake-cute .sc-kv b{color:#0f172a}

/* —— 游戏棋盘 —— */
#snake-cute .sc-shell{position:relative;margin:auto;width:min(92vw,540px);aspect-ratio:1/1;border-radius:20px;padding:var(--gap);background:linear-gradient(145deg,#fff,#f9fafb);border:1px solid var(--bd);box-shadow:var(--shadow)}
#snake-cute .sc-grid{position:relative;width:100%;height:100%;display:grid;gap:var(--gap)}
/* 🌈 美化后的格子：柔和粉蓝瓷砖质感（去掉生硬描边） */
#snake-cute .sc-cell{border:none;border-radius:9px;position:relative;overflow:hidden;background:
  radial-gradient(140% 120% at 25% 20%, #ffffff 0 30%, #f5f7fb 60%, #eef2f7 100%);
  box-shadow: inset 0 1px 0 rgba(255,255,255,.6), 0 0 0 1px rgba(17,24,39,.03);
}
/* 蛇：奶龙风配色（奶绿+奶白），圆润可爱 */
#snake-cute .sc-cell.sc-snake{background:linear-gradient(180deg,#e3fff3,#baf8df);box-shadow:inset 0 0 10px rgba(0,0,0,.08), 0 0 0 1px rgba(16,185,129,.14)}
#snake-cute .sc-cell.sc-head{background:linear-gradient(180deg,#f6fff9,#c9f6e6);box-shadow:inset 0 0 12px rgba(0,0,0,.10), 0 0 0 1px rgba(5,150,105,.2)}
#snake-cute .sc-eye{position:absolute;width:22%;height:22%;background:#0f172a;border-radius:50%;top:28%;left:24%;box-shadow:0 0 0 3px #fff}
#snake-cute .sc-eye.sc-r{left:auto;right:24%}
#snake-cute .sc-blush{position:absolute;width:26%;height:18%;background:rgba(244,114,182,.45);border-radius:50%;bottom:22%;left:22%}
#snake-cute .sc-blush.sc-r{left:auto;right:22%}
/* 奶龙风小角 */
