/* 倒计时环（conic-gradient） */
#snake-cute .sc-ring{position:absolute;inset:6%;border-radius:50%;pointer-events:none;opacity:.9;mix-blend-mode:multiply;/* 可选，和底色融合 */
  --pct:100; /* 0-100 剩余百分比 */
  --c: rgba(245,158,11,.9); /* 环颜色默认金色，JS 会覆盖 */
  background: conic-gradient(var(--c) calc(var(--pct)*1%), transparent 0);
  -webkit-mask: radial-gradient(circle, transparent calc(50% - 9px), #000 calc(50% - 8px), #000 52%, transparent 52%);
          mask: radial-gradient(circle, transparent calc(50% - 9px), #000 calc(50% - 8px), #000 52%, transparent 52%);
}

/* 覆盖层 */
