#snake-cute .sc-mini-btn{appearance:none;border:none;background:transparent;cursor:pointer;font-size:18px}

/* 触控方向键（移动端显示） */
#snake-cute .sc-dpad{display:none;gap:10px;place-items:center;grid-template-columns:repeat(3,64px);grid-template-rows:repeat(3,64px);margin:auto}
#snake-cute .sc-padbtn{width:64px;height:64px;border-radius:16px;border:1px solid var(--bd);background:rgba(255,255,255,.92);box-shadow:var(--shadow);display:grid;place-items:center;font-size:22px;user-select:none}
