/* 嵌入超极简模式：只保留棋盘 + HUD */
#snake-cute.sc-embed .sc-wrap{max-width:none;padding:0;background:none}
#snake-cute.sc-embed .sc-panel{background:transparent;border:none;box-shadow:none;padding:0}
#snake-cute.sc-embed .sc-head,
#snake-cute.sc-embed .sc-meta,
#snake-cute.sc-embed .sc-bar,
#snake-cute.sc-embed .sc-foot,
#snake-cute.sc-embed .sc-dpad{display:none}
#snake-cute.sc-embed .sc-hud{display:flex}
