(()=>{
  // —— 配置 ——
  const STORE = 'snake:cute:best:v1';
  const SIZES = [16,20,24]; let sizeIdx=1; let N=SIZES[sizeIdx];
  const ACCEL=5; // 每吃5个普通豆加速一次
  const BONUS_LIFE=8000; // 奖励豆寿命 ms
  const BONUS_WARN=1800; // 倒计时闪烁阈值 ms

  // —— 获取元素 ——
  const root = document.getElementById('snake-cute');
  const grid = document.getElementById('sc-grid');
  const shell = document.getElementById('sc-shell');
  const startBtn=document.getElementById('sc-start');
  const pauseBtn=document.getElementById('sc-pause');
  const sizeBtn=document.getElementById('sc-size');
  const slowBtn=document.getElementById('sc-slow');
  const audioBtn=document.getElementById('sc-audio');
  const scoreEl=document.getElementById('sc-score');
  const scoreMini=document.getElementById('sc-score-mini');
  const bestEl=document.getElementById('sc-best');
  const speedEl=document.getElementById('sc-speed');
  const sizeKv=document.getElementById('sc-sizekv');
  const over=document.getElementById('sc-over');
  const finalEl=document.getElementById('sc-final');
  const replayBtn=document.getElementById('sc-replay');
  const shareBtn=document.getElementById('sc-share');
  const toast=document.getElementById('sc-toast');
  const audioMiniBtn=document.getElementById('sc-audio-mini');
  const miniPauseBtn=document.getElementById('sc-mini-pause');
  const miniSlowBtn=document.getElementById('sc-mini-slow');

  // —— 状态 ——
  let cells=[]; let snake=[]; let dir={x:1,y:0}; let queued=null;
  let apple=null; 
  let bonus=null; // {x,y,type:'score'|'slow'|'trim'|'rainbow',expire:number}
  let score=0; let best=Number(localStorage.getItem(STORE)||0);
  let running=false, paused=false; let tick=150; let last=0; let rafId=null;
  let audioOn=false; let ac=null; // WebAudio
  let eaten=0; // 吃到普通豆计数
  let slow=false; // 慢速模式
  let baseMs=150, minMs=60, stepMs=10; // 动态速度参数
  let multBonus=0; // 叠加倍率偏移（蓝色豆 -0.2× 作用）

  bestEl.textContent=best;

  // —— 工具 ——
  const Idx=(x,y)=> y*N+x;
  const inB=(x,y)=> x>=0&&y>=0&&x{try{navigator.vibrate&&navigator.vibrate(ms)}catch(_){} };
  const toastMsg=(t)=>{toast.textContent=t;toast.classList.add('sc-show');setTimeout(()=>toast.classList.remove('sc-show'),900)};
  const setCompact=()=>{ const compact=window.matchMedia('(max-width:520px), (hover: none)').matches; root.classList.toggle('sc-compact', compact); };

  // —— 声音 ——
  const ensureAC=()=>{ if(!audioOn) return null; if(ac) return ac; try{ ac=new (window.AudioContext||window.webkitAudioContext)(); }catch(_){ ac=null;} return ac; };
  const beep=(freq=760, dur=80)=>{ if(!audioOn) return; const ctx=ensureAC(); if(!ctx) return; const o=ctx.createOscillator(); const g=ctx.createGain(); o.type='triangle'; o.frequency.value=freq; o.connect(g); g.connect(ctx.destination); const t=ctx.currentTime; g.gain.setValueAtTime(0.0001,t); g.gain.exponentialRampToValueAtTime(0.15,t+0.01); o.start(); o.stop(t+dur/1000); g.gain.exponentialRampToValueAtTime(0.0001, t + (dur/1000) - 0.02); };
  const setAudioUI=()=>{ audioBtn && (audioBtn.textContent = (audioOn?'🔊 音效开':'🔈 音效关')); audioMiniBtn && (audioMiniBtn.textContent = (audioOn?'🔊':'🔈')); };

  // —— 速度模型（蓝色豆软上限：最低 0.9×） ——
  const recomputeTick=()=>{
    const k=Math.floor(eaten/ACCEL);
    const accelTick=Math.max(minMs, baseMs - k*stepMs);
    const accelMult=baseMs/accelTick; // ≥1.0
    const effMult=Math.max(0.9, accelMult + multBonus); // 软下限 0.9×
    tick = Math.max(minMs, baseMs / effMult);
    speedEl.textContent = effMult.toFixed(1)+'x';
  };
  const setSlowUI=()=>{ slowBtn && (slowBtn.textContent = slow ? '🐢 慢速开' : '🐢 慢速关'); miniSlowBtn && (miniSlowBtn.style.opacity = slow ? '1' : '.6'); };
  const toggleSlow=()=>{ slow=!slow; if(slow){ baseMs=220; minMs=110; stepMs=10; } else { baseMs=150; minMs=60; stepMs=10; } recomputeTick(); setSlowUI(); toastMsg(slow ? '🐢 慢速模式（不生成蓝色减速豆）' : '⚡️ 正常速度'); };

  // —— 棋盘 ——
  const build=()=>{ grid.innerHTML=''; grid.style.gridTemplateColumns=`repeat(${N},1fr)`; grid.style.gridTemplateRows=`repeat(${N},1fr)`; cells=[]; for(let i=0;i{ let p; do{ p={x:Math.floor(Math.random()*N),y:Math.floor(Math.random()*N)} } while((bonus && p.x===bonus.x && p.y===bonus.y) || snake.some(s=>s.x===p.x&&s.y===p.y)); apple=p; };
  const placeBonus=(type)=>{ let p; do{ p={x:Math.floor(Math.random()*N),y:Math.floor(Math.random()*N)} } while((apple && p.x===apple.x && p.y===apple.y) || snake.some(s=>s.x===p.x&&s.y===p.y)); bonus={x:p.x,y:p.y,type,expire:performance.now()+BONUS_LIFE}; };
  // 概率分布：正常 70% 金(+3) / 10% 蓝(-0.2×) / 15% 绿(-5) / 5% 彩虹(+10)
  // 慢速模式不出蓝 => 80% 金 / 15% 绿 / 5% 彩虹
  const spawnRandomBonus=()=>{ 
    const r=Math.random();
    if(slow){ if(r target){ snake.pop(); }
        particles(head.x,head.y,'#bbf7d0'); vib(20); beep(680,90); toastMsg('🪄 身长 -5');
      } else if(bonus.type==='rainbow'){
        score += 10; scoreEl.textContent=score; scoreMini.textContent=score; if(score>best){best=score;bestEl.textContent=best;localStorage.setItem(STORE,best)}
        particles(head.x,head.y,'#ffd6f9'); vib(50); beep(1200,120); toastMsg('🌈 +10 分！');
      }
      bonus=null;
    }

    if(!onApple && !onBonus){ snake.pop(); }

    draw();
  };

  const start=()=>{ running=true; paused=false; pauseBtn.textContent='⏸ 暂停'; pauseBtn.setAttribute('aria-pressed','false'); miniPauseBtn.textContent='⏸'; toastMsg('开始！'); score=0; eaten=0; multBonus=0; scoreEl.textContent=score; scoreMini.textContent=score; dir={x:1,y:0}; queued=null; const m=Math.floor(N/2); snake=[{x:m,y:m}]; bonus=null; placeApple(); draw(); if(rafId) cancelAnimationFrame(rafId); rafId=requestAnimationFrame(loop); over.style.display='none'; recomputeTick(); beep(600,60); };
  const pause=()=>{ if(!running) return; paused=!paused; const nowPaused=paused; toastMsg(nowPaused?'已暂停':'继续游戏'); if(audioOn) beep(nowPaused?300:500,60); 
    // 可见文本切换，同时用 aria-pressed 暴露状态，并保持 aria-label 稳定（更利于可访问性）
    pauseBtn.textContent = nowPaused ? '▶ 继续' : '⏸ 暂停';
    miniPauseBtn.textContent = nowPaused ? '▶' : '⏸';
    pauseBtn.setAttribute('aria-pressed', String(nowPaused));
  };
  const gameOver=()=>{ running=false; paused=false; over.style.display='flex'; finalEl.textContent=score; vib([60,40,60]); beep(180,140); };

  // —— 输入 ——
  const setDir=(d)=>{ if(!running||paused) return; if(isOpp(d,dir)) return; queued=d; };
  window.addEventListener('keydown',e=>{
    if(["ArrowUp","ArrowDown","ArrowLeft","ArrowRight"," "].includes(e.key)) e.preventDefault();
    if(e.key===' ') { pause(); return; }
    if(e.key==='ArrowUp') setDir({x:0,y:-1});
    if(e.key==='ArrowDown') setDir({x:0,y:1});
    if(e.key==='ArrowLeft') setDir({x:-1,y:0});
    if(e.key==='ArrowRight') setDir({x:1,y:0});
  },{passive:false});

  let sx=0,sy=0; grid.addEventListener('touchstart',e=>{const t=e.touches[0]; sx=t.clientX; sy=t.clientY;},{passive:true});
  grid.addEventListener('touchend',e=>{const t=e.changedTouches[0]; const dx=t.clientX-sx, dy=t.clientY-sy; if(Math.abs(dx)>Math.abs(dy)){ setDir(dx>0?{x:1,y:0}:{x:-1,y:0}); } else { setDir(dy>0?{x:0,y:1}:{x:0,y:-1}); }},{passive:true});
  ['touchstart','touchmove','touchend'].forEach(evt=>document.body.addEventListener(evt,e=>{ if(e.target.closest('#snake-cute .sc-shell')) e.preventDefault(); },{passive:false}));

  document.querySelectorAll('#snake-cute .sc-padbtn[data-dir]').forEach(b=>b.addEventListener('click',()=>{
    const d=b.dataset.dir; if(d==='up') setDir({x:0,y:-1}); if(d==='down') setDir({x:0,y:1}); if(d==='left') setDir({x:-1,y:0}); if(d==='right') setDir({x:1,y:0});
  }));

  // —— 按钮 ——
  startBtn.addEventListener('click',start); replayBtn.addEventListener('click',start); pauseBtn.addEventListener('click',pause);
  shareBtn.addEventListener('click',async()=>{ const text=`我在糖心贪吃蛇里拿到 ${score} 分！🍬🐍`; if(navigator.share){ try{await navigator.share({text})}catch(_){} } else { await navigator.clipboard?.writeText(text); toastMsg('已复制分数'); }});
  sizeBtn.addEventListener('click',()=>{ sizeIdx=(sizeIdx+1)%SIZES.length; N=SIZES[sizeIdx]; sizeBtn.textContent=`🧩 ${N}×${N}`; sizeKv.textContent=`${N}×${N}`; build(); draw(); });

  const toggleAudio=()=>{ audioOn=!audioOn; setAudioUI(); if(audioOn) ensureAC(); };
  audioBtn.addEventListener('click',toggleAudio);
  audioMiniBtn.addEventListener('click',toggleAudio);
  miniPauseBtn.addEventListener('click',pause);
  slowBtn.addEventListener('click',toggleSlow);
  miniSlowBtn.addEventListener('click',toggleSlow);

  // —— 初始化 ——
  const init=()=>{ setCompact(); sizeKv.textContent=`${N}×${N}`; sizeBtn.textContent=`🧩 ${N}×${N}`; build(); draw(); setAudioUI(); setSlowUI(); recomputeTick(); };
  init();
  window.addEventListener('resize', setCompact);

  // 如果容器一开始就有 .sc-embed 类，启用嵌入超极简样式
  if(root.classList.contains('sc-embed')){ setCompact(); }
})();




这学期其实还有一件很重要的事情一直在影响我这个学期的生活主基调的，但是其实我不是很想说太多，或许下次开个情感专题的时候再仔细写写吧。总的来说就是，当时自己确实太年轻了，太难拿起和放下自己的情感了，自己拉扯自己，不断内耗自己，不停地做无谓的emo。对，这一点也不像我。我也觉得我自己当时真的好蠢，但是客观来说，这件事情过后也让自己再一次成熟了不少，难道说情关才是让一个未经世事的小孩子迈向成熟的重要关卡吗？哈哈哈哈哈。不过很搞笑，出现问题的第二天就是期末考试，我真的觉得期末考试太重要，所以心都在考试上了，第二天狠狠考过之后心情反倒好了很多，难道说我真的是先天卷王圣体吗？还是说我真的拎得清不同事情的重要性呢？我在这学期结束后，不仅仅是因为课程太多任务太多导致自己极其疲惫，还因为这段经历让我十分心累，就真的整个人再期末结束之后是一个身心俱疲的状态，真的很想回家好好休息休息，真的是第一次感觉整个人这么累。当时坐在回家的飞机上真的感觉很解脱很爽啊，终于可以为自己忙碌而混乱的生活暂时画上一个句号了。
